INSERT INTO `static_pages` (`id`, `title_uz`, `title_ru`, `title_en`, `sub_title_uz`, `sub_title_ru`, `sub_title_en`, `text_uz`, `text_ru`, `text_en`, `slug`, `seen`, `image`, `user_id`, `created_at`, `updated_at`) VALUES
(21, 'Sirdaryo viloyati Iqtisodiy taraqqiyot va kambag‘allikni qisqartirish  bosh boshqarmasida vakant lavozimlarga qo‘yilgan malaka talablari', 'Квалификационные требования на вакантные должности  в Главном управлении Экономического развития и сокращения бедности Сырдарьинской области', 'Sirdaryo viloyati Iqtisodiy taraqqiyot va kambag‘allikni qisqartirish  bosh boshqarmasida vakant lavozimlarga qo‘yilgan malaka talablari', NULL, NULL, NULL, '<table class=\"table-head-extant\">\r\n<tbody>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Ma\'lumot</span></td>\r\n<td><span style=\"font-size: 14pt;\">Oliy ma\'lumot</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Mutaxassislik</span></td>\r\n<td><span style=\"font-size: 14pt;\">Iqtisodiyot</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Mehnat staji</span></td>\r\n<td><span style=\"font-size: 14pt;\">Bosh mutaxassis lavozimi uchun 3 yildan kam bo\'lmagan, bo\'lim boshlig\'i o\'rinbosari lapvozimi uchun 3-5 yil va bo\'lim boshlig\'i lavozimi uchun 5 yildan ortiq iqtisodiyot yo\'nalishi sohasidagi ish stajiga ega bo\'lishi kerak</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Xorijiy tilni bilish darajasi</span></td>\r\n<td><span style=\"font-size: 14pt;\">Xorijiy tillarni o\'zlashtirgan (ustunlik beradi)</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Kompyuter savodxonligi</span></td>\r\n<td><span style=\"font-size: 14pt;\">Kompyuter savodxonligiga ega bo\'lgan, Microsoft Office dasturi (Word, Excel, Power Point)da ishlay olishi shart</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Qo\'shimcha talablar</span></td>\r\n<td><span style=\"font-size: 14pt;\">Strategik va tahliliy fikr yurita olishi, Qarorlarni tez qabul qila olishi hamda ularni bajara olishi, ish vaqtidan samarali foydalana olishi va tadbirlarni rejalashtirish ko\'nikmalariga va rahbarlik salohiyatiga ega, odob-ahloq normalari, shuningdek rahbarlik etikasiga rioya etadigan; oxirgi uch yil davomida ishlagan korxona va tashkilotlaridan salbiy sabablarga ko\'ra belgilangan tartibda bo\'shatilmagan bo\'lishi lozim, viloyatimizning ijtimoiy-iqtisodiy salohiyatini oshirishda o\'z hissasini qo\'shishga tayyor bo\'lgan mas\'uliyatli, vatanparvar xodim</span></td>\r\n</tr>\r\n</tbody>\r\n</table>', '<table class=\"table-head-extant\">\r\n<thead>\r\n<tr></tr>\r\n</thead>\r\n<tbody>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Образование</span></td>\r\n<td><span style=\"font-size: 14pt;\">Высшее образование</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Специальность</span></td>\r\n<td><span style=\"font-size: 14pt;\">Экономика</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Трудовой стаж</span></td>\r\n<td><span style=\"font-size: 14pt;\">Для должности главного специалиста кандидат должен иметь опыт работы в области экономики не менее 3-х лет, 3-5 лет - для должности заместителя начальника отдела и более 5 лет - для должности начальника отдела</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Уровень знания иностранных языков</span></td>\r\n<td><span style=\"font-size: 14pt;\">Владение иностранными языками приветствуется</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Компьютерная грамотность</span></td>\r\n<td><span style=\"font-size: 14pt;\">Должен владеть навыками работы на компьютере, уметь свободно работать в оффисных программах Microsoft Office (Word, Excel, Power Point).</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Дополнительные требования</span></td>\r\n<td><span style=\"font-size: 14pt;\">Стратегическое и аналитическое мышление, умение быстро принимать решения и реализовывать их, умеющий эффективно использовать рабочее время и обладающий навыками планирования мероприятий и лидерским потенциалом, соблюдающий этические нормы поведения; ответственность, патриотич-ность, готовность внести свой вклад в повышение социально-экономического потенциала нашей области</span></td>\r\n</tr>\r\n</tbody>\r\n</table>', '<table class=\"table-head-extant\">\r\n<tbody>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Ma\'lumot</span></td>\r\n<td><span style=\"font-size: 14pt;\">Oliy ma\'lumot</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Mutaxassislik</span></td>\r\n<td><span style=\"font-size: 14pt;\">Iqtisodiyot</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Mehnat staji</span></td>\r\n<td><span style=\"font-size: 14pt;\">Bosh mutaxassis lavozimi uchun 3 yildan kam bo\'lmagan, bo\'lim boshlig\'i o\'rinbosari lapvozimi uchun 3-5 yil va bo\'lim boshlig\'i lavozimi uchun 5 yildan ortiq iqtisodiyot yo\'nalishi sohasidagi ish stajiga ega bo\'lishi kerak</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Xorijiy tilni bilish darajasi</span></td>\r\n<td><span style=\"font-size: 14pt;\">Xorijiy tillarni o\'zlashtirgan (ustunlik beradi)</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Kompyuter savodxonligi</span></td>\r\n<td><span style=\"font-size: 14pt;\">Kompyuter savodxonligiga ega bo\'lgan, Microsoft Office dasturi (Word, Excel, Power Point)da ishlay olishi shart</span></td>\r\n</tr>\r\n<tr>\r\n<td><span style=\"font-size: 14pt;\">Qo\'shimcha talablar</span></td>\r\n<td><span style=\"font-size: 14pt;\">Strategik va tahliliy fikr yurita olishi, Qarorlarni tez qabul qila olishi hamda ularni bajara olishi, ish vaqtidan samarali foydalana olishi va tadbirlarni rejalashtirish ko\'nikmalariga va rahbarlik salohiyatiga ega, odob-ahloq normalari, shuningdek rahbarlik etikasiga rioya etadigan; oxirgi uch yil davomida ishlagan korxona va tashkilotlaridan salbiy sabablarga ko\'ra belgilangan tartibda bo\'shatilmagan bo\'lishi lozim, viloyatimizning ijtimoiy-iqtisodiy salohiyatini oshirishda o\'z hissasini qo\'shishga tayyor bo\'lgan mas\'uliyatli, vatanparvar xodim</span></td>\r\n</tr>\r\n</tbody>\r\n</table>', 'sirdaryo-viloyati-iqtisodiy-taraqqiyot-va-kambagallikni-qisqartirish-bosh-boshqarmasida-vakant-lavozimlarga-qoyilgan-malaka-talablari', 35, NULL, 1, '2022-08-30 11:27:26', '2022-09-07 23:50:01');
